package corejava;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//do while
		//=========
		//do {
		//	statment
		//	dec/inc
		//} while(condition);
		
		
		// first statments will exexcutes and checks the conditions
		//ex -1
		int i=1;
		do {
			System.out.println(i);
			i++;
		}while(i<=10);
		 //ex-2
		int j=10;
		do {
			System.out.println(j);
			j++;
		}while(j<=10);
		
	}

}
